var volume = 0;

// Setup http request parameters
switch (msg.bose.function) {
    case 'key':
        msg.payload = '<key state=' + msg.bose.keyState + ' sender=Gabbo>' + msg.bose.key + '</key>';
        node.status({fill: 'blue', shape: 'dot', text: msg.bose.system.name + ' - Function: ' +
            msg.bose.function + ', Key State: ' + msg.bose.keyState + ', Key: ' + msg.bose.key});
        break;
    case 'setZone':
        msg.payload = msg.bose.option;
        break;
    case 'volume':
        switch(msg.bose.volume) {
            case 'low':
                msg.bose.volume = msg.bose.system.lowVolume;
                break;
            case 'medium':
                msg.bose.volume = msg.bose.system.mediumVolume;
                break;
            case 'high':
                msg.bose.volume = msg.bose.system.highVolume;
                break;
            case 'max':
                msg.bose.volume = msg.bose.system.maxVolume;
        }
        switch(msg.bose.option) {
            case 'set':
                volume = msg.bose.volume;
                break;
            case 'offset':
                volume = flow.get('bose_volume', 'memory') + msg.bose.volume;
        }
        if (volume > msg.bose.system.maxVolume) {
            volume = msg.bose.system.maxVolume;
        } else if (volume < 0) {
            volume = 0;
        }
        msg.payload = '<volume>' + volume + '</volume>';
        node.status({fill: 'blue', shape: 'dot', text: msg.bose.system.name + ' - Function.option: ' +
            msg.bose.function + '.' + msg.bose.option + ', volume: ' + volume});
}
msg.method = msg.bose.method;
return msg;

if (typeof msg.payload.method === 'string') {
    var name = msg.payload.name;

    msg.bose = {
        "method": msg.payload.method,
        "function": msg.payload.function,
        "option": msg.payload.option,
        "key": msg.payload.key,
        "keyState": msg.payload.keyState,
        "volume": msg.payload.volume,
        "delay": msg.payload.delay,
        "notification": msg.payload.notification,
        "volumeNotification": msg.payload.volumeNotification,
        "system": {}
    }

    if (name == 'select') {
        name = flow.get('bose_name')
    }

    msg.bose.system = flow.get(name.replace(/ /g, '_'));

    // Setup node status
    switch (msg.bose.method) {
        case 'GET':
            node.status({fill: 'blue', shape: 'dot', text: name + ' - Function: ' +
                msg.bose.function});
            break;
        case 'POST':
            if (msg.bose.function == 'volume') {
                node.status({fill: 'blue', shape: 'dot', text: name + ' - Function.option: ' +
                    msg.bose.function + '.' +  msg.bose.option + ', volume: ' + msg.bose.volume});
            } else {
                node.status({fill: 'blue', shape: 'dot', text: name + ' - Function: ' +
                    msg.bose.function + ', Key: ' + msg.bose.key});
            }
    }

    // Setup optional delay
    if (typeof msg.payload.delay  !== 'undefined') {
        msg.delay = msg.payload.delay;
    } else {
        msg.delay = 0;
    }

    // Set url
    msg.url = 'http://' + msg.bose.system.ipAddress + ':8090/' + msg.bose.function;
    return msg;
}

